@import url('https://fonts.googleapis.com/css2?family=Unbounded:wght@200..900&display=swap');
* {
 margin: 0;
 padding: 0;
 box-sizing: border-box;
 font-family: 'Unbounded', sans-serif;
}
:component .app-root {
 background: #0e3652;
 color: #eee;
 height: 100vh;
}
:component .app-topbar, 
:component .app-body, 
:component .app-result {
 padding: 20px;
}
:component input {
 background: #0002;
 color: #fff;
 border-radius: 4px;
 border: 0;
 outline: 0;
 padding: 12px 16px;
}
:component .app-result__item {
 margin-bottom: 8px;
}