@import url('https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,100;0,300;0,400;0,500;0,700;0,900;1,100;1,300;1,400;1,500;1,700;1,900&display=swap');
* {
 margin: 0;
 padding: 0;
 box-sizing: border-box;
 font-family: 'Montserrat', sans-serif;
}
:component .app-root {
 background: #1f2020;
 color: #eee;
 height: 100vh;
}
:component .app-topbar, 
:component .app-body, 
:component .app-result {
 padding: 20px;
}
:component input {
 background: #0002;
 color: #fff;
 border-radius: 4px;
 border: 0;
 outline: 0;
 padding: 12px 16px;
}
:component .app-result__item {
 margin-bottom: 8px;
}